let timerId;const noteTitle=document.querySelector(".__title input"),noteBody=document.querySelector(".__body textarea"),selectAllButton=document.querySelector("._select-all"),deleteAllButton=document.querySelector("._delete-all"),confirmDeleteButton=document.querySelector("._delete-confirm"),saveToLocalButton=document.querySelector("._save-to-local");function selectAll(){noteBody.select()}function showDeleteConfirm(){confirmDeleteButton.style.display="inline",deleteAllButton.style.display="none",timerId=setTimeout(()=>{confirmDeleteButton.style.display="none",deleteAllButton.style.display="inline"},2500)}function confirmDelete(){confirmDeleteButton.style.display="none",deleteAllButton.style.display="inline",noteTitle.value="",noteBody.value="",clearInterval(timerId),saveTitle(),saveText()}function downloadNote(){let e=noteTitle.value||"Simple Note",t=noteBody.value,l=document.createElement("a");l.setAttribute("href","data:text/plain;charset=utf-8,"+encodeURIComponent(t)),l.setAttribute("download",e),l.style.display="none",document.body.appendChild(l),l.click(),document.body.removeChild(l)}const note_title="Note_Title",note_text="Note_Text";function saveTitle(){localStorage.setItem(note_title,noteTitle.value)}function saveText(){localStorage.setItem(note_text,noteBody.value)}function loadData(){let e=localStorage.getItem(note_title)||"";noteTitle.value=e;let t=localStorage.getItem(note_text)||"";noteBody.value=t}noteTitle.addEventListener("input",saveTitle),noteBody.addEventListener("input",saveText),selectAllButton.addEventListener("click",selectAll),deleteAllButton.addEventListener("click",showDeleteConfirm),confirmDeleteButton.addEventListener("click",confirmDelete),saveToLocalButton.addEventListener("click",downloadNote),document.addEventListener("DOMContentLoaded",()=>{loadData()});